    function set_theme_option(){
	  	$import_code = "";
	    $import_code = base64_decode($import_code);
	    $import_code = unserialize($import_code);
	    $import_code = $this->recursive_array_replace('http://accesspressthemes.com/import/apmag-v2/', get_site_url() , $import_code );
	    update_option('accesspress-mag-theme', $import_code);
    }

    function set_menu() {	
		global $wpdb;
		$table_db_name = $wpdb->prefix . "terms";
		$rows = $wpdb->get_results("SELECT * FROM $table_db_name WHERE name='Menu 1' OR name='Footer Menu' OR name='Top Menu' OR name='Top Menu Right' ",ARRAY_A);
        $menu_ids = array();
		foreach($rows as $row) {
			$menu_ids[$row["name"]] = $row["term_id"] ;
		}
		set_theme_mod( 'nav_menu_locations', array_map( 'absint', array( 'primary' =>$menu_ids['Menu 1'], 'footer_menu' => $menu_ids['Footer Menu'], 'top_menu' => $menu_ids['Top Menu'], 'top_menu_right' => $menu_ids['Top Menu Right'] ) ) );
	}

	function set_widgets() {
		$sidebars_widgets_code = ' ';
		$sidebars_widgets_code = base64_decode($sidebars_widgets_code);
	    $sidebars_widgets_code = unserialize($sidebars_widgets_code);
	    update_option('sidebars_widgets', $sidebars_widgets_code);

	    $all_widgets_code = ' ';
	    $all_widgets_code = base64_decode($all_widgets_code);
	    $all_widgets_code = unserialize($all_widgets_code);
	    
		foreach ($all_widgets_code as $option) {
			$option_array = unserialize($option->option_value);
			$option_array = $this->recursive_array_replace('http://accesspressthemes.com/import/apmag-v2', get_site_url() , $option_array );
			update_option($option->option_name, $option_array);
		}
	}
}